using Components;using System;using MessagePack;using HECSFramework.Serialize;using Commands;namespace HECSFramework.Core{	[MessagePackObject, Serializable]	public partial struct AppVersionComponentResolver : IResolver<AppVersionComponent>, IResolver<AppVersionComponentResolver,AppVersionComponent>, IData	{		[Key(0)]		public int Version;		public AppVersionComponentResolver In(ref AppVersionComponent appversioncomponent)		{			this.Version = appversioncomponent.Version;			return this;		}		public void Out(ref Entity entity)		{			var local = entity.GetComponent<AppVersionComponent>();			Out(ref local);		}		public void Out(ref AppVersionComponent appversioncomponent)		{			appversioncomponent.Version = this.Version;		}	}}