using Components;using System;using MessagePack;using HECSFramework.Serialize;using Commands;namespace HECSFramework.Core{	[MessagePackObject, Serializable]	public partial struct RadiusComponentResolver : IResolver<RadiusComponent>, IResolver<RadiusComponentResolver,RadiusComponent>, IData	{		[Key(0)]		public float Radius;		public RadiusComponentResolver In(ref RadiusComponent radiuscomponent)		{			this.Radius = radiuscomponent.Radius;			return this;		}		public void Out(ref Entity entity)		{			var local = entity.GetComponent<RadiusComponent>();			Out(ref local);		}		public void Out(ref RadiusComponent radiuscomponent)		{			radiuscomponent.Radius = this.Radius;		}	}}