using HECSFramework.Core;using Components;namespace Strategies{	public sealed class StateComponentSetRemainingTime : InterDecision	{		public override string TitleOfNode { get; } = "StateComponentSetRemainingTime";		[Connection(ConnectionPointType.In, "<Single> Set RemainingTime")]		public GenericNode<float> SetRemainingTime;		protected override void Run(Entity entity)		{			entity.GetOrAddComponent<StateTimerComponent>().RemainingTime = SetRemainingTime.Value(entity);			Next.Execute(entity);		}	}}